mapper.select:
SELECT ocd.DB_SEQ, ocd.TITLE FROM DB_HOMEPAGE.OM_CONTACT_DB as ocd WHERE ocd.PROVE_STATE = '1'
AND ocd.CONTACT_DB_GUBUN = #{sc_type}
AND ( ocd.TITLE LIKE '%' || #{sc_text} || '%' OR ocd.CONTENT LIKE '%' || #{sc_text} || '%' OR ocd.QUST_CNTN LIKE '%' || #{sc_text} || '%' )
AND ocd.REG_DATE <= (SELECT ocd2.REG_DATE FROM DB_HOMEPAGE.OM_CONTACT_DB as ocd2 WHERE ocd2.DB_SEQ = #{db_seq}) AND ocd.DB_SEQ < #{db_seq}
ORDER BY ocd.DB_SEQ DESC LIMIT 1

SELECT ocd.DB_SEQ, ocd.TITLE FROM DB_HOMEPAGE.OM_CONTACT_DB as ocd WHERE ocd.PROVE_STATE = '1'
AND ocd.CONTACT_DB_GUBUN = #{sc_type}
AND ( ocd.TITLE LIKE '%' || #{sc_text} || '%' OR ocd.CONTENT LIKE '%' || #{sc_text} || '%' OR ocd.QUST_CNTN LIKE '%' || #{sc_text} || '%' )
AND ocd.REG_DATE >= (SELECT ocd2.REG_DATE FROM DB_HOMEPAGE.OM_CONTACT_DB as ocd2 WHERE ocd2.DB_SEQ = #{db_seq}) AND ocd.DB_SEQ > #{db_seq}
ORDER BY ocd.DB_SEQ ASC LIMIT 1
