mapper.update:
UPDATE DB_HOMEPAGE.HP_BOARD AS hbd SET hbd.BOARD_VIEW = hbd.BOARD_VIEW + 1 WHERE hbd.CONF_ID IN
#{obj.value}
AND hbd.BOARD_NUM = #{board_num}

mapper.select:
SELECT hbd.BOARD_NUM, hbd.BOARD_TITLE, DATE_FORMAT(hbd.BOARD_REG_DATE, '%Y-%m-%d') as BOARD_REG_DATE, hbd.BOARD_VIEW, hbd.BOARD_CONTENTS, hbd.IS_TOP FROM DB_HOMEPAGE.HP_BOARD as hbd WHERE hbd.CONF_ID IN
#{obj.value}
AND hbd.BOARD_NUM = #{board_num}

SELECT COUNT(*) as count FROM DB_HOMEPAGE.HP_BOARD as hbd WHERE hbd.CONF_ID IN
#{obj.value}
AND hbd.IS_TOP = 1
AND hbd.BOARD_REG_DATE <= (SELECT hbd2.BOARD_REG_DATE FROM DB_HOMEPAGE.HP_BOARD AS hbd2 WHERE hbd2.BOARD_NUM = #{board_num}) AND hbd.BOARD_NUM != #{board_num}
LIMIT 1
